import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const API_URL = 'https://pokeapi.co/api/v2/pokemon';

function Generation2() {
  const [pokemons, setPokemons] = useState([]);

  useEffect(() => {
    async function getPokemons() {
      // Hacer una solicitud a la API para obtener cada Pokémon de la segunda generación
      for (let i = 100; i < 200; i++) {
        const response = await axios.get(`${API_URL}/${i}`);
        const pokemon = {
          name: response.data.name,
          type: response.data.types[0].type.name,
          imageUrl: response.data.sprites.front_default,
          index: response.data.id
        };
        setPokemons(prevPokemons => [...prevPokemons, pokemon]);
      }
    }
    getPokemons();
  }, []);

  return (
    <GenStyle>
        <div>
        {pokemons.map(pokemon => (
            <div key={pokemon.name}>
            <img src={pokemon.imageUrl} alt={pokemon.name} />
            <p>{pokemon.name}</p>
            <p>{pokemon.type}</p>
            <p>{pokemon.index}</p>
            </div>
        ))}
        </div>
    </GenStyle>
  );
}

export default Generation2;

const GenStyle = styled.div`

div{
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    padding: 0.5px 5px;

    
}
img{
    margin-right: 12rem;
    width: 4em;
    height: 4em;
    border-radius: 50%;
    box-shadow: 2px 2px 1px 1px #fda038;
    margin-top: .2rem ;
    background-color: #f2f2f2;
    margin-left: 5%;
}
`;
