import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const API_URL = 'https://pokeapi.co/api/v2/pokemon';

export const Generation2= (props) => {
  const [pokemons, setPokemons] = useState({});
  const [typeImageUrl, setTypeImageUrl] = useState('');

  useEffect(() => {
    async function getPokemons() {
      for (let i = 152; i < 251; i++) {
        const response = await axios.get(`${API_URL}/${i}`);
        setPokemons({
          name: response.data.name,
          type: response.data.types[0].type.name,
          imageUrl: response.data.sprites.front_default,
          index: response.data.id
        });
       
      }
    }
    getPokemons();
  }, [props.index]);

  useEffect(() => {
    async function getTypeImage() {
      // Hacer una solicitud a la API de Pokémon para obtener la imagen del tipo
      const response = await axios.get(`${API_URL}/type/${pokemon.type}`);
      setTypeImageUrl(response.data.sprites.front_default);
    }
    getTypeImage();
  }, [pokemon.type]);

  return (
    <GenStyle>
        <div className='row'>
            {pokemons.map(pokemon => (
                <div className='card' key={pokemon.name}>
                    <p>Nº {pokemon.index}</p>
                    <div>
                        <img src={pokemon.imageUrl} alt={pokemon.name} />
