import React, { useState } from "react";
import Burger from "./component/Burger";
import styled from "styled-components";
import { Route, Routes, Link } from "react-router-dom";
import { Contact } from "../MyRoutes/contact/Contact";
import { Generation2 } from "../MyRoutes/contact/Generation/Generation2";
import { Generation1 } from "../MyRoutes/contact/Generation/Generation1";



export default function NavBar(props) {
  const [clicked, setClicked] = useState(false)
  const handleClick = () => {setClicked(!clicked)}
  
    return (
      <div>
        <NavStyle>
          <nav className="navbar navbar-dark fixed-top">
          <Link to="/pokedexJSX"><img alt="../img/Pokédex_logo.png" src={require("../img/Pokédex_logo.png")}/></Link>
            
            <div className="burger">
              <Burger clicked={clicked} handleClick={handleClick}/>
            </div >
            <div className={`navlink ${clicked ? 'active' : ''}`}>
              <div>
                <button href="/">
                  <Link to="/Generation2">Generation2</Link>
                </button>
                <button href="/">
                  <Link to="/Contact">Contact</Link>
                </button>
                <button href="/">Search</button>
              </div>
            </div>

          </nav>

          <div>
            <Routes>
              <Route path="/pokedexJSX" element={<Generation1 />} />
              <Route path="Generation2" element={<Generation2 />} />
              <Route path="Contact" element={<Contact />} />
            </Routes>
          </div>
        </NavStyle>
      </div>
    );
  }

const NavStyle = styled.nav`

.navbar{
  color: #fff;
  display: flex;
  padding: 0.5rem 1rem;
  
  font-weight: bold;
  align-items: center;  
}
span{
  margin-right: 1rem;
  justify-content: space-between;
  color: #fff;
  text-decoration: none;
  justify-items: flex-end;
  
  
}
button{
  margin-right: 1rem;
  justify-content: space-between;
  color: #fff;
  text-decoration: none;
  justify-items: flex-end;
  flex-direction: column;
  
}

/*
Link{
  text-decoration: none;
  color: #fff;
}
*/


img{
  width: 6.8rem;
  height: 3rem;
  border-radius: 0;
  box-shadow: 0 0 0;
  margin-top: .2rem ;
  background-color: transparent;
  margin-right: .1rem
}
h2{
 justify-content: flex-start;
  color: yellowgreen;
  padding-left: 1rem;
 
  font-weight: bold;
  
}


.navlink{
  position: absolute;
  top: -700px;
  left: -2000px;
  margin-left: auto;
  margin-right: auto;
  text-align: center;
  button{
    display: block;
    
  }

  @media(min-width: 750px){
    position: initial;
    margin: 0;
    button{
      display: inline;

    }
  }
}
.navlink.active{
  width: 100%;
  display: flex;
  position: relative;
  margin-left: auto;
  margin-right: auto;
  top: 40%;
  left: 40%;
  right: 40%;
  text-align: center;
  align-content: center;
}

.burger{
  @media(min-width: 750px){
      display: none;
      
  }     
}
`
