import React from 'react'
import styled from 'styled-components';
import axios from 'axios';
import { useState, useEffect } from 'react';

const API_URL = 'https://pokeapi.co/api/v2/pokemon';

export const Generation2 = () => {
    const [pokemons, setPokemons] = useState([]);

        useEffect(() => {
        async function getPokemons() {
            for (let i = 152; i < 251; i++) {
            const response = await axios.get(`${API_URL}/${i}`);
            const pokemon = {
                name: response.data.name,
                type: response.data.types[0].type.name,
                imageUrl: response.data.sprites.front_default,
                index: response.data.id
            };
            setPokemons(pokemons => [...pokemons]);
            }
        }
        getPokemons();
        }, []);

        return (
    <GenStyle>
           {pokemons.map(pokemon => (
            <div className="col-md-3 col-sm-6 mb-3" key={pokemon.name}>
                <p>{pokemon.index}</p>
                <img src={pokemon.imageUrl} alt={pokemon.name} />
                <p>{pokemon.name}</p>
                <p>{pokemon.type}</p>
            </div>
        ))}
    </GenStyle>
  )
}
const GenStyle = styled.div`

div{
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    padding: 0.5px 5px;

    
}
img{
    margin-right: 12rem;
    width: 4em;
    height: 4em;
    border-radius: 50%;
    box-shadow: 2px 2px 1px 1px #fda038;
    margin-top: .2rem ;
    background-color: #f2f2f2;
    margin-left: 5%;
}
`;
