import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const API_URL = 'https://pokeapi.co/api/v2/pokemon';

export const Generation2= (props) => {
    const [pokemon, setPokemon] = useState({});
    const [typeImageUrl, setTypeImageUrl] = useState('');
  
    useEffect(() => {
      async function getPokemon() {
        const response = await axios.get(`${API_URL}/${props.index}`);
        setPokemon({
          name: response.data.name,
          type: response.data.types[0].type.name,
          imageUrl: response.data.sprites.front_default,
          index: response.data.id
        });
      }
      getPokemon();
    }, [props.index]);
  
    useEffect(() => {
      async function getTypeImage() {
        const response = await axios.get(`${API_URL}/type/${pokemon.type}`);
        setTypeImageUrl(response.data.sprites.front_default);
      }
      getTypeImage();
    }, [pokemon.type]);
  
    return (
      <div>
        <img src={pokemon.imageUrl} alt={pokemon.name} />
        <p>{pokemon.name}</p>
        <img src={typeImageUrl} alt={pokemon.type} />
        <p>{pokemon.type}</p>
        <p>{pokemon.index}</p>
      </div>
    );
}

const GenStyle = styled.div`

.row{
    min-height: 90vh;
    display: flex;
    flex-wrap: wrap;  
    align-items: center;
    margin: 15px;

    padding: 25px;
    padding-top: 7rem;

    row-gap: 5px;
}
.card{  
    padding: 0.5px 5px;
    text-align: center;
    border-radius: 5%;
    box-shadow: 0 3px 5px #312f30;
    background-color: #312f30;
    color: #f2f2f2;
    width: 25rem;
    height: auto;
    margin: 10px;
   
    
}
h3{
    background-color: #f2f2f2;
    border-radius: 5%;
    box-shadow: 1px 1px 1px 1px;
    width: 80%;
    margin-top: .4rem ;
    color: #fda038 ;
    margin-right: 10%;
    margin-left: 10%;
    
}
img{
    margin-right: 12rem;
    width: 4em;
    height: 4em;
    border-radius: 50%;
    box-shadow: 2px 2px 1px 1px #fda038;
    margin-top: .2rem ;
    background-color: #f2f2f2;
    margin-left: 5%;
}
`;
